package Chapter3;

public class EX07 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 쉬프트 연산자
		int a = 7;
		int b = a >> 2; // 왼쪽에 0 두칸
		int c = a << 2; // 오른쪽에 0 두칸
		System.out.println("a = " + a);
		System.out.println("b = " + b);
		System.out.println("c = " + c);
	}

}
