package Chapter5;

public class EX02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 예제 2)
		// 문자열을 반복문 횟수를 역순으로 하여 10번 출력
		for(int i = 10; 1 <= i; i--) {
			System.out.println("java");
		}
		
		// 문자열을 반복문 횟수를 역순으로 하여 10번 순서 확인 및 출력
		for(int i = 10; 1 <= i; i--) {
			System.out.println("java" + i);
		}
	}

}
